 JRequest::getCmd('add','');		if(!empty($add)){			$add=1;		}else{			$add=0;		}		if(empty($product_id)){			$product_id = JRequest::getCmd('cid',0);		}		$cart_product_id = JRequest::getCmd('cart_product_id',0);		$quantity = JRequest::getInt('quantity',1);		if(hikashop_loadUser() != null || $cart_type != 'wishlist'){			if(!empty($product_id)){				$type = JRequest::getWord('type','product');				if($type=='product'){					$product_id=(int)$product_id;				}				$status = $class->update($product_id,$quantity,$add,$type);			}elseif(!empty($cart_product_id)){				$status = $class->update($cart_product_id,$quantity,$add,'item');			}else{				$formData = JRequest::getVar( 'item', array(), '', 'array' );				if(!empty($formData)){					$class->update($formData,0,$add,'item');				}else{					$formData = JRequest::getVar( 'data', array(), '', 'array' );					if(!empty($formData)){						$class->update($formData,0,$add);					}				}			}		}		$app->setUserState(HIKASHOP_COMPONENT.'.'.$cart_type.'_new', '1');		if(@$class->errors && $tmpl!='component'){			if(!empty($_SERVER['HTTP_REFERER'])){				if(strpos($_SERVER['HTTP_REFERER'],HIKASHOP_LIVE)===false && preg_match('#^https?://.*#',$_SERVER['HTTP_REFERER'])) return false;				$app->redirect( str_replace('&popup=1','',$_SERVER['HTTP_REFERER']));			}else{				echo '<html><head><script type="text/javascript">history.back();</script></head><body></body></html>';				exit;			}		}		$app->setUserState( HIKASHOP_COMPONENT.'.shipping_method', null);		$app->setUserState( HIKASHOP_COMPONENT.'.shipping_id', null);		$app->setUserState( HIKASHOP_COMPONENT.'.shipping_data', null);		$app->setUserState( HIKASHOP_COMPONENT.'.payment_method', null);		$app->setUserState( HIKASHOP_COMPONENT.'.payment_id', null);		$app->setUserState( HIKASHOP_COMPONENT.'.payment_data', null);		$config =& hikashop_config();		$checkout = JRequest::getString('checkout','');		if(!empty($checkout)){			global $Itemid;			$url = 'checkout';			if(!empty($Itemid)){				$url.='&Itemid='.$Itemid;			}			$url = hikashop_completeLink($url,false,true);			$this->setRedirect($url);		}else if($cart_type == 'wishlist'){			$app->setUserState( HIKASHOP_COMPONENT.'.popup_cart_type','wishlist');			if(hikashop_loadUser() == null){				$url = JRequest::getVar('return_url','');				if(!empty($url)){					$url=base64_decode(urldecode($url));				}				$url = str_replace(array('&popup=1','?popup=1'),'',$url);				if($config->get('redirect_url_after_add_cart','stay_if_cart') != 'ask_user')					$app->enqueueMessage(JText::_('LOGIN_REQUIRED_FOR_WISHLISTS'));				if($tmpl != 'component') {					if(!empty($_SERVER['HTTP_REFERER'])) {						if(strpos($_SERVER['HTTP_REFERER'],HIKASHOP_LIVE)===false && preg_match('#^https?://.*#',$_SERVER['HTTP_REFERER']))							return false;						if($config->get('redirect_url_after_add_cart','stay_if_cart') == 'ask_user')							$app->enqueueMessage(JText::_('LOGIN_REQUIRED_FOR_WISHLISTS'));						$app->redirect( str_replace('&popup=1','',$_SERVER['HTTP_REFERER']));					}				}else{					echo 'notLogged';					exit;				}			}else{				$redirectConfig = $config->get('redirect_url_after_add_cart','stay_if_cart');				$url='';				$stay = 0;				switch($redirectConfig){					case 'ask_user':						$url = JRequest::getVar('return_url','');						if(!empty($url)){							$url=base64_decode(urldecode($url));						}						$url = str_replace(array('&popup=1','?popup=1'),'',$url);						if(JRequest::getInt('popup',0) && empty($_COOKIE['popup']) || JRequest::getInt('quantity',0)){							if(strpos($url,'?')){								$url.='&';							}else{								$url.='?';							}							$url.='popup=1';							$app->setUserState( HIKASHOP_COMPONENT.'.popup','1');						}						JRequest::setVar('cart_type','wishlist');						break;					case 'stay':						$stay = 1;						break; //$stay = 1; && $url ='';					case 'checkout':						break; //$stay = 0; && $url ='';					case 'stay_if_cart':					default:						$module = JModuleHelper::getModule('hikashop_wishlist',false);						if($module != null){							$stay = 1;						}						break;				}				if($redirectConfig != 'checkout'){					$module = JModuleHelper::getModule('hikashop_wishlist',false);					$params = new HikaParameter( @$module->params );					if(!empty($module)){						$module_options = $config->get('params_'.$module->id);					}					if(empty($module_options)){						$module_options = $config->get('default_params');					}					$data = $params->get('hikashopwishlistmodule');					if(HIKASHOP_J30 && (empty($data) || !is_object($data))){						$db = JFactory::getDBO();						$query = 'SELECT params FROM '.hikashop_table('modules',false).' WHERE id = '.(int)$module->id;						$db->setQuery($query);						$itemData = json_decode($db->loadResult());						if(!empty($itemData->hikashopwishlistmodule) && is_object($itemData->hikashopwishlistmodule)){							$data = $itemData->hikashopwishlistmodule;							$params->set('hikashopwishlistmodule',$data);						}					}					if(!empty($data) && is_object($data)){						foreach($data as $k => $v){							$module_options[$k] = $v;						}					}					foreach($module_options as $key => $optionElement){						$params->set($key,$optionElement);					}					if(!empty($module)){						foreach(get_object_vars($module) as $k => $v){							if(!is_object($v)){								$params->set($k,$v);							}						}						$params->set('from','module');					}					$params->set('return_url',$url);					$params->set('cart_type','wishlist');					$js ='';					hikashop_getLayout('product','cart',$params,$js);				}			}			if(empty($url)){				global $Itemid;				if(isset($from_id))$cart_id = $from_id;				if(JRequest::getInt('new_'.$cart_type.'_id',0)!= 0 && JRequest::getInt('delete',0) == 0)$cart_id = JRequest::getInt('new_'.$cart_type.'_id',0);				$cart = $class->get($cart_id,false,$cart_type);				if(!empty($cart) && (int)$cart_id != 0){					$url = 'cart&task=showcart&cart_type=wishlist&cart_id='.$cart_id.'&Itemid='.$Itemid;				}else{					$url = 'cart&task=showcarts&cart_type=wishlist&Itemid='.$Itemid;				}				$url = hikashop_completeLink($url,false,true);			}			if(!isset($stay))				$stay = JRequest::getInt('stay',0);			if($stay == 0){				if(hikashop_disallowUrlRedirect($url)) return false;				if(JRequest::getVar('from_form',true)){					JRequest::setVar('cart_type','wishlist');					$this->setRedirect($url);					return false;				}else{					ob_clean();					echo 'URL|'.$url;					exit;				}			}else{				echo '<html><head><script type="text/javascript">history.back();</script></head><body></body></html>';				exit;			}		}else{			$app->setUserState( HIKASHOP_COMPONENT.'.popup_cart_type','cart');			$url = JRequest::getVar('return_url','');			if(empty($url)){				$url = JRequest::getVar('url','');				$url = urldecode($url);			}else{				$url = base64_decode(urldecode($url));			}			$url = str_replace(array('&popup=1','?popup=1'),'',$url);			if(hikashop_disallowUrlRedirect($url))				$url = '';			if(empty($url)){				global $Itemid;				$url = 'checkout';				if(!empty($Itemid)){					$url.='&Itemid='.$Itemid;				}				$url = hikashop_completeLink($url,false,true);			}			$params = new HikaParameter( @$module->params );			if($tmpl=='component' && $config->get('redirect_url_after_add_cart','stay_if_cart') != 'checkout'){				$js ='';				jimport('joomla.application.module.helper');				global $Itemid;				if(isset($Itemid) && empty($Itemid)){					$Itemid=null;					JRequest::setVar('Itemid',null);				}				$module = JModuleHelper::getModule('hikashop_cart',false);				$config =& hikashop_config();				$params = new HikaParameter( @$module->params );				if(!empty($module)){					$module_options = $config->get('params_'.$module->id);				}				if(empty($module_options)){					$module_options = $config->get('default_params');				}				$data = $params->get('hikashopcartmodule');				if(HIKASHOP_J30 && (empty($data) || !is_object($data))){					$db = JFactory::getDBO();					$query = 'SELECT params FROM '.hikashop_table('modules',false).' WHERE id = '.(int)$module->id;					$db->setQuery($query);					$itemData = json_decode($db->loadResult());					if(!empty($itemData->hikashopcartmodule) && is_object($itemData->hikashopcartmodule)){						$data = $itemData->hikashopcartmodule;						$params->set('hikashopcartmodule',$data);					}				}				if(!empty($data) && is_object($data)){					foreach($data as $k => $v){						$module_options[$k] = $v;					}				}				foreach($module_options as $key => $optionElement){					$params->set($key,$optionElement);				}				if(!empty($module)){					foreach(get_object_vars($module) as $k => $v){						if(!is_object($v)){							$params->set($k,$v);						}					}					$params->set('from','module');				}				$params->set('return_url',$url);				hikashop_getLayout('product','cart',$params,$js);				return true;			}else{				$config =& hikashop_config();				$url = str_replace(array('&popup=1','?popup=1'),'',$url);				if(JRequest::getInt('popup',0) || (@JRequest::getInt('quantity',0) && $config->get('redirect_url_after_add_cart','stay_if_cart') == 'ask_user')){					if(strpos($url,'?')){						$url.='&';					}else{						$url.='?';					}					$url.='popup=1';					$app->setUserState( HIKASHOP_COMPONENT.'.popup','1');				}				if(JRequest::getInt('hikashop_ajax', 0) == 0) { // $config->get('ajax_add_to_cart','1') == '0'){					$this->setRedirect($url);					return false;				}else{					ob_clean();					if($params->get('from','module') != 'module' || $config->get('redirect_url_after_add_cart','stay_if_cart') == 'checkout'){						echo 'URL|'.$url;						exit;					}else{						$this->setRedirect($url);						return false;					}				}			}		}	}	public function ajax_get_list_category_product(){	    echo "sfdsfsd";	}	public function ajax_get_list_product_by_category_id()	{		$input=JFactory::getApplication()->input;		$menu=JFactory