<?php$image_helper = hikashop_get('helper.image');$debug=JUtility::get_debug();$list=new stdClass();$list->categories=$this->rows;foreach($list->categories as &$category) {    $category->medium_image = $image_helper->getThumbnail($category->category_medium_image_file_path, array(500, 500), array('default' => true), true);    $category->link=hikashop_contentLink('category&task=listing&cid='.$category->category_id.'&name='.$category->alias,$category->category_id);}$list->products=$this->products;foreach($list->products as &$product) {    foreach($product->images as &$image) {        $image = $image_helper->getThumbnail($image->file_path, array(500, 500), array('default' => true), true);    }    $link='product&task=show&cid=' . $product->product_id . '&name=' . $product->alias . $this->itemid . $this->category_pathway;    $product->link=hikashop_contentLink($link.'&partner_id='.$this->user->user_id, $this->row);}echo json_encode($list);?>