<?php/** * JComments - Joomla Comment System * * @version 3.0 * @package JComments * @author Sergey M. Litvinov (smart@joomlatune.ru) * @copyright (C) 2006-2013 by Sergey M. Litvinov (http://www.joomlatune.ru) * @license GNU/GPL: http://www.gnu.org/copyleft/gpl.html */defined('_JEXEC') or die;/** * JComments System Plugin Helper */class JCommentsSystemPluginHelper{    public static function getBaseUrl()    {        return JURI::root(true);    }    public static function getCoreJS()    {        return JURI::root(true) . '/components/com_jcomments/js/jcomments-v2.3.js';    }    public static function getAjaxJS()    {        return JURI::root(true) . '/components/com_jcomments/libraries/joomlatune/ajax.js';    }    public static function getCSS($isRTL = false, $template = '')    {        $app = JFactory::getApplication('site');        if (empty($template)) {            $config = JCommentsCfg::getInstance();            $template = $config->get('template');        }        $cssName = $isRTL ? 'style_rtl.css' : 'style.css';        $cssFile = $cssName;        $cssPath = JPATH_SITE . '/templates/' . $app->getTemplate() . '/html/com_jcomments/' . $template . '/' . $cssName;        $cssUrl = JURI::root(true) . '/templates/' . $app->getTemplate() . '/html/com_jcomments/' . $template . '/' . $cssFile;        if (!is_file($cssPath)) {            $cssPath = JPATH_SITE . '/components/com_jcomments/tpl/' . $template . '/' . $cssName;            $cssUrl = JURI::root(true) . '/components/com_jcomments/tpl/' . $template . '/' . $cssFile;            if ($isRTL && !is_file($cssPath)) {                $cssUrl = '';            }        }        return $cssUrl;    }}